pragma solidity ^0.8.19;

interface IVe {
    enum DepositType {
        DEPOSIT_FOR_TYPE,
        CREATE_LOCK_TYPE,
        INCREASE_LOCK_AMOUNT,
        INCREASE_UNLOCK_TIME,
        MERGE_TYPE
    }

    struct Point {
        int128 bias;
        int128 slope; // # -dweight / dt
        uint ts;
        uint blk; // block
    }

//    function increaseF_WSTETH(uint _WSTETHFee) external;

//    function increaseF_SIM(uint _SIMFee) external;

    function lockedAmounts(uint veId, address stakingToken) external view returns (uint);

    function lockedDerivedAmount(uint veId) external view returns (uint);

