// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.19;

/// @notice Arithmetic library with operations for fixed-point numbers.
/// @author Solmate (https://github.com/Rari-Capital/solmate/blob/main/src/utils/FixedPointMathLib.sol)
/// @author Inspired by USM (https://github.com/usmfum/USM/blob/master/contracts/WadMath.sol)
library FixedPointMathLib {
  /*//////////////////////////////////////////////////////////////
  //SIMPLIFIED FIXED POINT OPERATIONS
  //////////////////////////////////////////////////////////////*/

  uint internal constant WAD = 1e18; // The scalar of ETH and most ERC20s.

  function mulWadDown(uint x, uint y) internal pure returns (uint) {
    return mulDivDown(x, y, WAD);
    // Equivalent to (x * y) / WAD rounded down.
  }

  function mulWadUp(uint x, uint y) internal pure returns (uint) {
    return mulDivUp(x, y, WAD);
    // Equivalent to (x * y) / WAD rounded up.
  }

  function divWadDown(uint x, uint y) internal pure returns (uint) {
    return mulDivDown(x, WAD, y);
    // Equivalent to (x * WAD) / y rounded down.
  }

  function divWadUp(uint x, uint y) internal pure returns (uint) {
    return mulDivUp(x, WAD, y);
    // Equivalent to (x * WAD) / y rounded up.
  }

  function positiveInt128(int128 value) internal pure returns (int128) {
    return value < 0 ? int128(0) : value;
  }

  /*//////////////////////////////////////////////////////////////
  //LOW LEVEL FIXED POINT OPERATIONS
  //////////////////////////////////////////////////////////////*/

  function mulDivDown(
    uint x,
    uint y,
    uint denominator
  ) internal pure returns (uint z) {
    assembly {
    // Store x * y in z for now.
      z := mul(x, y)

    // Equivalent to require(denominator != 0 && (x == 0 || (x * y) / x == y))
      if iszero(and(iszero(iszero(denominator)), or(iszero(x), eq(div(z, x), y)))) {
        revert(0, 0)
      }

    // Divide z by the denominator.
      z := div(z, denominator)
    }
  }

  function mulDivUp(
    uint x,
    uint y,
    uint denominator
  ) internal pure returns (uint z) {
    assembly {
    // Store x * y in z for now.
      z := mul(x, y)

    // Equivalent to require(denominator != 0 && (x == 0 || (x * y) / x == y))
      if iszero(and(iszero(iszero(denominator)), or(iszero(x), eq(div(z, x), y)))) {
        revert(0, 0)
      }

    // First, divide z - 1 by the denominator and add 1.
    // We allow z - 1 to underflow if z is 0, because we multiply the
    // end result by 0 if z is zero, ensuring we return 0 if z is zero.
      z := mul(iszero(iszero(z)), add(div(sub(z, 1), denominator), 1))
    }
  }

  function rpow(
    uint x,
    uint n,
    uint scalar
  ) internal pure returns (uint z) {
    assembly {
      switch x
      case 0 {
        switch n
        case 0 {
        // 0 ** 0 = 1
          z := scalar
        }
        default {
        // 0 ** n = 0
          z := 0
        }
      }
      default {
        switch mod(n, 2)
        case 0 {
        // If n is even, store scalar in z for now.
          z := scalar
        }
        default {
        // If n is odd, store x in z for now.
          z := x
        }

      // Shifting right by 1 is like dividing by 2.
        let half := shr(1, scalar)

        for {
        // Shift n right by 1 before looping to halve it.
          n := shr(1, n)
        } n {
        // Shift n right by 1 each iteration to halve it.
          n := shr(1, n)
        } {
        // Revert immediately if x ** 2 would overflow.
        // Equivalent to iszero(eq(div(xx, x), x)) here.
          if shr(128, x) {
            revert(0, 0)
          }

        // Store x squared.
          let xx := mul(x, x)

        // Round to the nearest number.
          let xxRound := add(xx, half)

        // Revert if xx + half overflowed.
          if lt(xxRound, xx) {
            revert(0, 0)
          }

        // Set x to scaled xxRound.
          x := div(xxRound, scalar)

        // If n is even:
          if mod(n, 2) {
          // Compute z * x.
            let zx := mul(z, x)

          // If z * x overflowed:
            if iszero(eq(div(zx, x), z)) {
            // Revert if x is non-zero.
              if iszero(iszero(x)) {
                revert(0, 0)
              }
            }

          // Round to the nearest number.
            let zxRound := add(zx, half)

          // Revert if zx + half overflowed.
            if lt(zxRound, zx) {
              revert(0, 0)
            }

          // Return properly scaled zxRound.
            z := div(zxRound, scalar)
          }
        }
      }
    }
  }

  /*//////////////////////////////////////////////////////////////
  // GENERAL NUMBER UTILITIES
  //////////////////////////////////////////////////////////////*/

  function sqrt(uint x) internal pure returns (uint z) {
    assembly {
    // Start off with z at 1.
      z := 1

    // Used below to help find a nearby power of 2.
      let y := x

    // Find the lowest power of 2 that is at least sqrt(x).
      if iszero(lt(y, 0x100000000000000000000000000000000)) {
        y := shr(128, y) // Like dividing by 2 ** 128.
        z := shl(64, z) // Like multiplying by 2 ** 64.
      }
      if iszero(lt(y, 0x10000000000000000)) {
        y := shr(64, y) // Like dividing by 2 ** 64.
        z := shl(32, z) // Like multiplying by 2 ** 32.
      }
      if iszero(lt(y, 0x100000000)) {
        y := shr(32, y) // Like dividing by 2 ** 32.
        z := shl(16, z) // Like multiplying by 2 ** 16.
      }
      if iszero(lt(y, 0x10000)) {
        y := shr(16, y) // Like dividing by 2 ** 16.
        z := shl(8, z) // Like multiplying by 2 ** 8.
      }
      if iszero(lt(y, 0x100)) {
        y := shr(8, y) // Like dividing by 2 ** 8.
        z := shl(4, z) // Like multiplying by 2 ** 4.
      }
      if iszero(lt(y, 0x10)) {
        y := shr(4, y) // Like dividing by 2 ** 4.
        z := shl(2, z) // Like multiplying by 2 ** 2.
      }
      if iszero(lt(y, 0x8)) {
      // Equivalent to 2 ** z.
        z := shl(1, z)
      }

    // Shifting right by 1 is like dividing by 2.
      z := shr(1, add(z, div(x, z)))
      z := shr(1, add(z, div(x, z)))
      z := shr(1, add(z, div(x, z)))
      z := shr(1, add(z, div(x, z)))
      z := shr(1, add(z, div(x, z)))
      z := shr(1, add(z, div(x, z)))
      z := shr(1, add(z, div(x, z)))

    // Compute a rounded down version of z.
      let zRoundDown := div(x, z)

    // If zRoundDown is smaller, use it.
      if lt(zRoundDown, z) {
        z := zRoundDown
      }
    }
  }
}

