    ) {
        _lockedAmount = lockedAmounts[veId][stakingToken];
        _lockedDerivedAmount = lockedDerivedAmount[veId];
        _lockedEnd = lockedEnd[veId];
    }

    function _incrementTokenIdAndGet() internal returns (uint){
        uint current = tokenId;
        tokenId = current + 1;
        return current + 1;
    }

    /// @notice Deposit `_value` tokens for `_to` and lock for `_lock_duration`
    /// @param _token Token for deposit. Should be whitelisted in this contract.
    /// @param _value Amount to deposit
    /// @param _lockDuration Number of seconds to lock tokens for (rounded down to nearest week)
    /// @param _to Address to deposit
    function _createLock(address _token, uint _value, uint _lockDuration, address _to) internal returns (uint) {
        require(_value > 0, "WRONG_INPUT");
        // Lock time is rounded down to weeks
        uint unlockTime = (block.timestamp + _lockDuration) / WEEK * WEEK;
        require(unlockTime > block.timestamp, "LOW_LOCK_PERIOD");
        require(unlockTime <= block.timestamp + MAX_TIME, "HIGH_LOCK_PERIOD");
        require(isValidToken[_token], "INVALID_TOKEN");

        uint _tokenId = _incrementTokenIdAndGet();
        _mint(_to, _tokenId);

        _depositFor(DepositInfo({
            stakingToken: _token,
            tokenId: _tokenId,
            value: _value,
            unlockTime: unlockTime,
            lockedAmount: 0,
            lockedDerivedAmount: 0,
            lockedEnd: 0,
            depositType: DepositType.CREATE_LOCK_TYPE
        }));
        return _tokenId;
    }

    /// @notice Deposit `_value` tokens for `_to` and lock for `_lock_duration`
    /// @param _token Token for deposit. Should be whitelisted in this contract.
    /// @param _value Amount to deposit
    /// @param _lockDuration Number of seconds to lock tokens for (rounded down to nearest week)
    /// @param _to Address to deposit
    function createLockFor(address _token, uint _value, uint _lockDuration, address _to)
    external nonReentrant override returns (uint) {
        return _createLock(_token, _value, _lockDuration, _to);
    }

    /// @notice Deposit `_value` tokens for `msg.sender` and lock for `_lock_duration`
    /// @param _value Amount to deposit
    /// @param _lockDuration Number of seconds to lock tokens for (rounded down to nearest week)
    function createLock(address _token, uint _value, uint _lockDuration) external nonReentrant returns (uint) {
        return _createLock(_token, _value, _lockDuration, msg.sender);
    }

    /// @notice Deposit `_value` additional tokens for `_tokenId` without modifying the unlock time
    /// @dev Anyone (even a smart contract) can deposit for someone else, but
    ///      cannot extend their locktime and deposit for a brand new user
    /// @param _token Token for deposit. Should be whitelisted in this contract.
    /// @param _tokenId ve token ID
    /// @param _value Amount of tokens to deposit and add to the lock
    function increaseAmount(address _token, uint _tokenId, uint _value) external nonReentrant override {
        require(_value > 0, "WRONG_INPUT");
        (uint _lockedAmount, uint _lockedDerivedAmount, uint _lockedEnd) = _lockInfo(_token, _tokenId);

        require(_lockedDerivedAmount > 0, "NFT_WITHOUT_POWER");
        require(_lockedEnd > block.timestamp, "EXPIRED");
        require(isValidToken[_token], "INVALID_TOKEN");

        _depositFor(DepositInfo({
            stakingToken: _token,
            tokenId: _tokenId,
            value: _value,
            unlockTime: 0,
            lockedAmount: _lockedAmount,
            lockedDerivedAmount: _lockedDerivedAmount,
            lockedEnd: _lockedEnd,
            depositType: DepositType.INCREASE_LOCK_AMOUNT
        }));
    }

    /// @notice Extend the unlock time for `_tokenId`
    /// @param _tokenId ve token ID
    /// @param _lockDuration New number of seconds until tokens unlock
    function increaseUnlockTime(uint _tokenId, uint _lockDuration) external nonReentrant returns (
        uint power,
        uint unlockDate
    )  {
        uint _lockedDerivedAmount = lockedDerivedAmount[_tokenId];
        uint _lockedEnd = lockedEnd[_tokenId];
        // Lock time is rounded down to weeks
        uint unlockTime = (block.timestamp + _lockDuration) / WEEK * WEEK;
        require(_lockedDerivedAmount > 0, "NFT_WITHOUT_POWER");
        require(_lockedEnd > block.timestamp, "EXPIRED");
        require(unlockTime > _lockedEnd, "LOW_UNLOCK_TIME");
        require(unlockTime <= block.timestamp + MAX_TIME, "HIGH_LOCK_PERIOD");
        require(isApprovedOrOwner(msg.sender, _tokenId), "NOT_OWNER");

        _depositFor(DepositInfo({
            stakingToken: address(0),
            tokenId: _tokenId,
            value: 0,
            unlockTime: unlockTime,
            lockedAmount: 0,
            lockedDerivedAmount: _lockedDerivedAmount,
            lockedEnd: _lockedEnd,
            depositType: DepositType.INCREASE_UNLOCK_TIME
        }));

        power = balanceOfNFT(_tokenId);
        unlockDate = lockedEnd[_tokenId];
    }

    /// @dev Merge two NFTs union their balances and keep the biggest lock time.
    function merge(uint _from, uint _to) external nonReentrant {
//        require(attachments[_from] == 0 && !isVoted(_from), "ATTACHED");
        require(_from != _to, "IDENTICAL_ADDRESS");
        require(_idToOwner[_from] == msg.sender && _idToOwner[_to] == msg.sender, "NOT_OWNER");

        uint lockedEndFrom = lockedEnd[_from];
        uint lockedEndTo = lockedEnd[_to];
        require(lockedEndFrom > block.timestamp && lockedEndTo > block.timestamp, "EXPIRED");
        uint end = lockedEndFrom >= lockedEndTo ? lockedEndFrom : lockedEndTo;
        uint oldDerivedAmount = lockedDerivedAmount[_from];

        uint length = tokens.length;
        // we should use the old one for properly calculate checkpoint for the new ve
        uint newLockedEndTo = lockedEndTo;
        for (uint i; i < length; i++) {
            address stakingToken = tokens[i];
            uint _lockedAmountFrom = lockedAmounts[_from][stakingToken];
            if (_lockedAmountFrom == 0) {
                continue;
            }
            lockedAmounts[_from][stakingToken] = 0;

            _depositFor(DepositInfo({
                stakingToken: stakingToken,
                tokenId: _to,
                value: _lockedAmountFrom,
                unlockTime: end,
                lockedAmount: lockedAmounts[_to][stakingToken],
                lockedDerivedAmount: lockedDerivedAmount[_to],
                lockedEnd: newLockedEndTo,
                depositType: DepositType.MERGE_TYPE
            }));

            // set new lock time to the current end lock
            newLockedEndTo = end;

            emit Merged(stakingToken, msg.sender, _from, _to);
        }

        lockedDerivedAmount[_from] = 0;
        lockedEnd[_from] = 0;

        // update checkpoint
        _checkpoint(CheckpointInfo(
            _from,
            oldDerivedAmount,
            0,
            lockedEndFrom,
            lockedEndFrom
        ));

        _burn(_from);
    }

    /// @dev Split given veNFT. A new NFT will have a given percent of underlying tokens.
    /// @param _tokenId ve token ID
    /// @param percent percent of underlying tokens for new NFT with denominator 1e18 (1-(100e18-1)).
    function split(uint _tokenId, uint percent) external nonReentrant {
//        require(attachments[_tokenId] == 0 && !isVoted(_tokenId), "ATTACHED");
        require(_idToOwner[_tokenId] == msg.sender, "NOT_OWNER");
        require(percent != 0 && percent < 100e18, "WRONG_INPUT");

        uint _lockedDerivedAmount = lockedDerivedAmount[_tokenId];
        uint oldLockedDerivedAmount = _lockedDerivedAmount;
        uint _lockedEnd = lockedEnd[_tokenId];

        require(_lockedEnd > block.timestamp, "EXPIRED");

        // crete new NFT
        uint _newTokenId = _incrementTokenIdAndGet();
        _mint(msg.sender, _newTokenId);

        // migrate percent of locked tokens to the new NFT
        uint length = tokens.length;
        for (uint i; i < length; ++i) {
            address stakingToken = tokens[i];
            uint _lockedAmount = lockedAmounts[_tokenId][stakingToken];
            if (_lockedAmount == 0) {
                continue;
            }
            uint amountForNewNFT = _lockedAmount * percent / 100e18;
            require(amountForNewNFT != 0, "LOW_PERCENT");

            uint newLockedDerivedAmount = _calculateDerivedAmount(
                _lockedAmount,
                _lockedDerivedAmount,
                _lockedAmount - amountForNewNFT,
                tokenWeights[stakingToken],
                IERC20Metadata(stakingToken).decimals()
            );

            _lockedDerivedAmount = newLockedDerivedAmount;

            lockedAmounts[_tokenId][stakingToken] = _lockedAmount - amountForNewNFT;

            // increase values for new NFT
            _depositFor(DepositInfo({
                stakingToken: stakingToken,
                tokenId: _newTokenId,
                value: amountForNewNFT,
                unlockTime: _lockedEnd,
                lockedAmount: 0,
                lockedDerivedAmount: lockedDerivedAmount[_newTokenId],
                lockedEnd: _lockedEnd,
                depositType: DepositType.MERGE_TYPE
            }));
        }

        // update derived amount
        lockedDerivedAmount[_tokenId] = _lockedDerivedAmount;

        // update checkpoint
        _checkpoint(CheckpointInfo(
            _tokenId,
            oldLockedDerivedAmount,
            _lockedDerivedAmount,
            _lockedEnd,
            _lockedEnd
        ));

        emit Split(_tokenId, _newTokenId, percent);
    }

    /// @notice Withdraw all staking tokens for `_tokenId`
    /// @dev Only possible if the lock has expired
    function withdrawAll(uint _tokenId) external {
        uint length = tokens.length;
        for (uint i; i < length; ++i) {
            address token = tokens[i];
            if (lockedAmounts[_tokenId][token] != 0) {
                withdraw(token, _tokenId);
            }
        }
    }

    /// @notice Withdraw given staking token for `_tokenId`
    /// @dev Only possible if the lock has expired
    function withdraw(address stakingToken, uint _tokenId) public nonReentrant {
        require(isApprovedOrOwner(msg.sender, _tokenId), "NOT_OWNER");
//        require(attachments[_tokenId] == 0 && !isVoted(_tokenId), "ATTACHED");

        (uint oldLockedAmount, uint oldLockedDerivedAmount, uint oldLockedEnd) =
                        _lockInfo(stakingToken, _tokenId);
        require(block.timestamp >= oldLockedEnd, "NOT_EXPIRED");
        require(oldLockedAmount > 0, "ZERO_LOCKED");


        uint newLockedDerivedAmount = _calculateDerivedAmount(
            oldLockedAmount,
            oldLockedDerivedAmount,
            0,
            tokenWeights[stakingToken],
            IERC20Metadata(stakingToken).decimals()
        );

        // if no tokens set lock to zero
        uint newLockEnd = oldLockedEnd;
        if (newLockedDerivedAmount == 0) {
            lockedEnd[_tokenId] = 0;
            newLockEnd = 0;
        }

        // update derived amount
        lockedDerivedAmount[_tokenId] = newLockedDerivedAmount;

        // set locked amount to zero, we will withdraw all
        lockedAmounts[_tokenId][stakingToken] = 0;

        // update checkpoint
        _checkpoint(CheckpointInfo(
            _tokenId,
            oldLockedDerivedAmount,
            newLockedDerivedAmount,
            oldLockedEnd,
            newLockEnd
        ));

        // Burn the NFT
        if (newLockedDerivedAmount == 0) {
            _burn(_tokenId);
        }

        _transferUnderlyingToken(stakingToken, msg.sender, oldLockedAmount);

        emit Withdraw(stakingToken, msg.sender, _tokenId, oldLockedAmount, block.timestamp);
    }

    // The following ERC20/minime-compatible methods are not real balanceOf and supply!
    // They measure the weights for the purpose of voting, so they don't represent
    // real coins.

    /// @notice Binary search to estimate timestamp for block number
    /// @param _block Block to find
    /// @param maxEpoch Don't go beyond this epoch
    /// @return Approximate timestamp for block
    function _findBlockEpoch(uint _block, uint maxEpoch) internal view returns (uint) {
        // Binary search
        uint _min = 0;
        uint _max = maxEpoch;
        for (uint i = 0; i < 128; ++i) {
            // Will be always enough for 128-bit numbers
            if (_min >= _max) {
                break;
            }
            uint _mid = (_min + _max + 1) / 2;
            if (_pointHistory[_mid].blk <= _block) {
                _min = _mid;
            } else {
                _max = _mid - 1;
            }
        }
        return _min;
    }

    /// @notice Get the current voting power for `_tokenId`
    /// @dev Adheres to the ERC20 `balanceOf` interface for Aragon compatibility
    /// @param _tokenId NFT for lock
    /// @param _t Epoch time to return voting power at
    /// @return User voting power
    function _balanceOfNFT(uint _tokenId, uint _t) internal view returns (uint) {
        uint _epoch = userPointEpoch[_tokenId];
        if (_epoch == 0) {
            return 0;
        } else {
            Point memory lastPoint = _userPointHistory[_tokenId][_epoch];
            require(_t >= lastPoint.ts, "WRONG_INPUT");
            lastPoint.bias -= lastPoint.slope * int128(int256(_t) - int256(lastPoint.ts));
            if (lastPoint.bias < 0) {
                lastPoint.bias = 0;
            }
            return uint(int256(lastPoint.bias));
        }
    }

    /// @dev Returns current token URI metadata
    /// @param _tokenId Token ID to fetch URI for.
    function tokenURI(uint _tokenId) external view override returns (string memory) {
        require(_idToOwner[_tokenId] != address(0), "TOKEN_NOT_EXIST");

        uint _lockedEnd = lockedEnd[_tokenId];
        return
            VeLogo.tokenURI(
            _tokenId,
            uint(int256(lockedDerivedAmount[_tokenId])),
            block.timestamp < _lockedEnd ? _lockedEnd - block.timestamp : 0,
            _balanceOfNFT(_tokenId, block.timestamp)
        );
    }

    /// @notice Measure voting power of `_tokenId` at block height `_block`
    /// @dev Adheres to MiniMe `balanceOfAt` interface: https://github.com/Giveth/minime
    /// @param _tokenId User's wallet NFT
    /// @param _block Block to calculate the voting power at
    /// @return Voting power
    function _balanceOfAtNFT(uint _tokenId, uint _block) internal view returns (uint) {
        // Copying and pasting totalSupply code because Vyper cannot pass by
        // reference yet
        require(_block <= block.number, "WRONG_INPUT");

        // Binary search
        uint _min = 0;
        uint _max = userPointEpoch[_tokenId];
        for (uint i = 0; i < 128; ++i) {
            // Will be always enough for 128-bit numbers
            if (_min >= _max) {
                break;
            }
            uint _mid = (_min + _max + 1) / 2;
            if (_userPointHistory[_tokenId][_mid].blk <= _block) {
                _min = _mid;
            } else {
                _max = _mid - 1;
            }
        }

        Point memory uPoint = _userPointHistory[_tokenId][_min];

        uint maxEpoch = epoch;
        uint _epoch = _findBlockEpoch(_block, maxEpoch);
        Point memory point0 = _pointHistory[_epoch];
        uint dBlock = 0;
        uint dt = 0;
        if (_epoch < maxEpoch) {
            Point memory point1 = _pointHistory[_epoch + 1];
            dBlock = point1.blk - point0.blk;
            dt = point1.ts - point0.ts;
        } else {
            dBlock = block.number - point0.blk;
            dt = block.timestamp - point0.ts;
        }
        uint blockTime = point0.ts;
        if (dBlock != 0 && _block > point0.blk) {
            blockTime += (dt * (_block - point0.blk)) / dBlock;
        }

        uPoint.bias -= uPoint.slope * int128(int256(blockTime - uPoint.ts));
        return uint(uint128(uPoint.bias.positiveInt128()));
    }

    /// @notice Calculate total voting power at some point in the past
    /// @param point The point (bias/slope) to start search from
    /// @param t Time to calculate the total voting power at
    /// @return Total voting power at that time
    function _supplyAt(Point memory point, uint t) internal view returns (uint) {
        Point memory lastPoint = point;
        uint ti = (lastPoint.ts / WEEK) * WEEK;
        for (uint i = 0; i < 255; ++i) {
            ti += WEEK;
            int128 dSlope = 0;
            if (ti > t) {
                ti = t;
            } else {
                dSlope = slopeChanges[ti];
            }
            lastPoint.bias -= lastPoint.slope * int128(int256(ti - lastPoint.ts));
            if (ti == t) {
                break;
            }
            lastPoint.slope += dSlope;
            lastPoint.ts = ti;
        }
        return uint(uint128(lastPoint.bias.positiveInt128()));
    }

    /// @notice Calculate total voting power
    /// @dev Adheres to the ERC20 `totalSupply` interface for Aragon compatibility
    /// @return Total voting power
    function totalSupplyAtT(uint t) public view returns (uint) {
        uint _epoch = epoch;
        Point memory lastPoint = _pointHistory[_epoch];
        return _supplyAt(lastPoint, t);
    }

    /// @notice Calculate total voting power at some point in the past
    /// @param _block Block to calculate the total voting power at
    /// @return Total voting power at `_block`
    function totalSupplyAt(uint _block) external view override returns (uint) {
        require(_block <= block.number, "WRONG_INPUT");
        uint _epoch = epoch;
        uint targetEpoch = _findBlockEpoch(_block, _epoch);

        Point memory point = _pointHistory[targetEpoch];
        // it is possible only for a block before the launch
        // return 0 as more clear answer than revert
        if (point.blk > _block) {
            return 0;
        }
        uint dt = 0;
        if (targetEpoch < _epoch) {
            Point memory pointNext = _pointHistory[targetEpoch + 1];
            // next point block can not be the same or lower
            dt = ((_block - point.blk) * (pointNext.ts - point.ts)) / (pointNext.blk - point.blk);
        } else {
            if (point.blk != block.number) {
                dt = ((_block - point.blk) * (block.timestamp - point.ts)) / (block.number - point.blk);
            }
        }
        // Now dt contains info on how far are we beyond point
        return _supplyAt(point, point.ts + dt);
    }

    function _burn(uint _tokenId) internal {
        address owner = ownerOf(_tokenId);
        // Clear approval
        approve(address(0), _tokenId);
        // Remove token
        _removeTokenFrom(owner, _tokenId);
        emit Transfer(owner, address(0), _tokenId);
    }
}

contracts/VeDistributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "./interfaces/IVe.sol";
import "./interfaces/IVeDistributor.sol";
import "./dependencies/ControllableV3.sol";

// https://github.com/tetu-io/tetu-contracts-v2/blob/master/contracts/ve/VeDistributor.sol
/// @title Contract for distributing rewards to ve holders.
///        Rewards will be staked to the veNFT without extending lock period.
///        Based on Solidly contract.
/// @author belbix
contract VeDistributor is ControllableV3, IVeDistributor {
  using SafeERC20 for IERC20;

  // for contract internal purposes, don't need to store in the interface
  struct ClaimCalculationResult {
    uint toDistribute;
    uint userEpoch;
    uint weekCursor;
    uint maxUserEpoch;
    bool success;
  }

  // *************************************************************
  //                        CONSTANTS
  // *************************************************************

  /// @dev Version of this contract. Adjust manually on each code modification.
  string public constant VE_DIST_VERSION = "1.0.0";
  uint internal constant WEEK = 7 * 86400;

  // *************************************************************
  //                        VARIABLES
  //                Keep names and ordering!
  //                 Add only in the bottom.
  // *************************************************************

  /// @dev Voting escrow token address
  IVe public ve;
  /// @dev Token for ve rewards
  address public override rewardToken;

  // --- CHECKPOINT

  /// @dev Cursor for the current epoch
  uint public activePeriod;
  /// @dev Tokens per week stored on checkpoint call. Predefined array size = max weeks size
  uint[1000000000000000] public tokensPerWeek;
  /// @dev Ve supply checkpoints. Predefined array size = max weeks size
  uint[1000000000000000] public veSupply;
  /// @dev Ve supply checkpoint time cursor
  uint public timeCursor;
  /// @dev Token balance updated on checkpoint/claim
  uint public tokenLastBalance;
  /// @dev Last checkpoint time
  uint public lastTokenTime;

  // --- CLAIM

  /// @dev Timestamp when this contract was inited
  uint public startTime;
  /// @dev veID => week cursor stored on the claim action
  mapping(uint => uint) public timeCursorOf;
  /// @dev veID => epoch stored on the claim action
  mapping(uint => uint) public userEpochOf;

  // *************************************************************
  //                        EVENTS
  // *************************************************************

  event CheckpointToken(
    uint time,
    uint tokens
  );

  event Claimed(
    uint tokenId,
    uint amount,
    uint claimEpoch,
    uint maxEpoch
  );

  // *************************************************************
  //                        INIT
  // *************************************************************

  /// @dev Proxy initialization. Call it after contract deploy.
  function init(
    address controller_,
    address _ve,
    address _rewardToken
  ) external initializer {
    __Controllable_init(controller_);
    _requireERC20(_rewardToken);
    _requireInterface(_ve, InterfaceIds.I_VE);

    uint _t = block.timestamp / WEEK * WEEK;
    startTime = _t;
    lastTokenTime = _t;
    timeCursor = _t;

    rewardToken = _rewardToken;
    ve = IVe(_ve);

//    IERC20(_rewardToken).safeApprove(_ve, type(uint).max);
  }

  // *************************************************************
  //                      CHECKPOINT
  // *************************************************************

  function checkpoint() external override {
    uint _period = activePeriod;
    // only trigger if new week
    if (block.timestamp >= _period + 1 weeks) {
      // set new period rounded to weeks
      activePeriod = block.timestamp / 1 weeks * 1 weeks;
      // checkpoint token balance that was just minted in veDist
      _checkpointToken();
      // checkpoint supply
      _checkpointTotalSupply();
    }
  }

  /// @dev Update tokensPerWeek value
  function _checkpointToken() internal {
    uint tokenBalance = IERC20(rewardToken).balanceOf(address(this));
    uint toDistribute = tokenBalance - tokenLastBalance;
    tokenLastBalance = tokenBalance;

    uint t = lastTokenTime;
    uint sinceLast = block.timestamp - t;
    lastTokenTime = block.timestamp;
    uint thisWeek = t / WEEK * WEEK;
    uint nextWeek = 0;

    // checkpoint should be called at least once per 20 weeks
    for (uint i = 0; i < 20; i++) {
      nextWeek = thisWeek + WEEK;
      if (block.timestamp < nextWeek) {
        tokensPerWeek[thisWeek] += adjustToDistribute(toDistribute, block.timestamp, t, sinceLast);
        break;
      } else {
        tokensPerWeek[thisWeek] += adjustToDistribute(toDistribute, nextWeek, t, sinceLast);
      }
      t = nextWeek;
      thisWeek = nextWeek;
    }
    emit CheckpointToken(block.timestamp, toDistribute);
  }

  /// @dev Adjust value based on time since last update
  function adjustToDistribute(
    uint toDistribute,
    uint t0,
    uint t1,
    uint sinceLast
  ) public pure returns (uint) {
    if (t0 <= t1 || t0 - t1 == 0 || sinceLast == 0) {
      return toDistribute;
    }
    return toDistribute * (t0 - t1) / sinceLast;
  }

  /// @dev Search in the loop given timestamp through ve points history.
  ///      Return minimal possible epoch.
  function findTimestampEpoch(IVe _ve, uint _timestamp) public view returns (uint) {
    uint _min = 0;
    uint _max = _ve.epoch();
    for (uint i = 0; i < 128; i++) {
      if (_min >= _max) break;
      uint _mid = (_min + _max + 2) / 2;
      IVe.Point memory pt = _ve.pointHistory(_mid);
      if (pt.ts <= _timestamp) {
        _min = _mid;
      } else {
        _max = _mid - 1;
      }
    }
    return _min;
  }

  /// @dev Search in the loop given timestamp through ve user points history.
  ///      Return minimal possible epoch.
  function findTimestampUserEpoch(
    IVe _ve,
    uint tokenId,
    uint _timestamp,
    uint maxUserEpoch
  ) public view returns (uint) {
    uint _min = 0;
    uint _max = maxUserEpoch;
    for (uint i = 0; i < 128; i++) {
      if (_min >= _max) break;
      uint _mid = (_min + _max + 2) / 2;
      IVe.Point memory pt = _ve.userPointHistory(tokenId, _mid);
      if (pt.ts <= _timestamp) {
        _min = _mid;
      } else {
        _max = _mid - 1;
      }
    }
    return _min;
  }

  /// @dev Return ve power at given timestamp
  function veForAt(uint _tokenId, uint _timestamp) external view returns (uint) {
    IVe _ve = ve;
    uint maxUserEpoch = _ve.userPointEpoch(_tokenId);
    uint epoch = findTimestampUserEpoch(_ve, _tokenId, _timestamp, maxUserEpoch);
    IVe.Point memory pt = _ve.userPointHistory(_tokenId, epoch);
    return uint(int256(_positiveInt128(pt.bias - pt.slope * (int128(int256(_timestamp - pt.ts))))));
  }

  /// @dev Call ve checkpoint and write veSupply at the current timeCursor
  function checkpointTotalSupply() external override {
    _checkpointTotalSupply();
  }

  function _checkpointTotalSupply() internal {
    IVe _ve = ve;
    uint t = timeCursor;
    uint roundedTimestamp = block.timestamp / WEEK * WEEK;
    _ve.checkpoint();

    // assume will be called more frequently than 20 weeks
    for (uint i = 0; i < 20; i++) {
      if (t > roundedTimestamp) {
        break;
      } else {
        uint epoch = findTimestampEpoch(_ve, t);
        IVe.Point memory pt = _ve.pointHistory(epoch);
        veSupply[t] = adjustVeSupply(t, pt.ts, pt.bias, pt.slope);
      }
      t += WEEK;
    }
    timeCursor = t;
  }

  /// @dev Calculate ve supply based on bias and slop for the given timestamp
  function adjustVeSupply(uint t, uint ptTs, int128 ptBias, int128 ptSlope) public pure returns (uint) {
    if (t < ptTs) {
      return 0;
    }
    int128 dt = int128(int256(t - ptTs));
    if (ptBias < ptSlope * dt) {
      return 0;
    }
    return uint(int256(_positiveInt128(ptBias - ptSlope * dt)));
  }

  // *************************************************************
  //                      CLAIM
  // *************************************************************

  /// @dev Return available to claim earned amount
  function claimable(uint _tokenId) external view returns (uint) {
    uint _lastTokenTime = lastTokenTime / WEEK * WEEK;
    ClaimCalculationResult memory result = _calculateClaim(_tokenId, ve, _lastTokenTime);
    return result.toDistribute;
  }

  /// @dev Claim rewards for given veID
  function claim(uint _tokenId) external override returns (uint) {
    IVe _ve = ve;
    if (block.timestamp >= timeCursor) _checkpointTotalSupply();
    uint _lastTokenTime = lastTokenTime;
    _lastTokenTime = _lastTokenTime / WEEK * WEEK;
    uint amount = _claim(_tokenId, _ve, _lastTokenTime);
    if (amount != 0) {
      IERC20(rewardToken).safeTransfer(IERC721(address(_ve)).ownerOf(_tokenId), amount);
//      _ve.increaseAmount(rewardToken, _tokenId, amount);
      tokenLastBalance -= amount;
    }
    return amount;
  }

  /// @dev Claim rewards for given veIDs
  function claimMany(uint[] memory _tokenIds) external returns (bool) {
    if (block.timestamp >= timeCursor) _checkpointTotalSupply();
    uint _lastTokenTime = lastTokenTime;
    _lastTokenTime = _lastTokenTime / WEEK * WEEK;
    IVe _votingEscrow = ve;
    uint total = 0;

    for (uint i = 0; i < _tokenIds.length; i++) {
      uint _tokenId = _tokenIds[i];
      if (_tokenId == 0) break;
      uint amount = _claim(_tokenId, _votingEscrow, _lastTokenTime);
      if (amount != 0) {
        IERC20(rewardToken).safeTransfer(IERC721(address(_votingEscrow)).ownerOf(_tokenId), amount);
//        _votingEscrow.increaseAmount(rewardToken, _tokenId, amount);
        total += amount;
      }
    }
    if (total != 0) {
      tokenLastBalance -= total;
    }

    return true;
  }

  function _claim(uint _tokenId, IVe _ve, uint _lastTokenTime) internal returns (uint) {
    ClaimCalculationResult memory result = _calculateClaim(_tokenId, _ve, _lastTokenTime);
    if (result.success) {
      userEpochOf[_tokenId] = result.userEpoch;
      timeCursorOf[_tokenId] = result.weekCursor;
      emit Claimed(_tokenId, result.toDistribute, result.userEpoch, result.maxUserEpoch);
    }
    return result.toDistribute;
  }

  function _calculateClaim(
    uint _tokenId,
    IVe _ve,
    uint _lastTokenTime
  ) internal view returns (ClaimCalculationResult memory) {
    uint userEpoch;
    uint maxUserEpoch = _ve.userPointEpoch(_tokenId);
    uint _startTime = startTime;

    if (maxUserEpoch == 0) {
      return ClaimCalculationResult(0, 0, 0, 0, false);
    }

    uint weekCursor = timeCursorOf[_tokenId];

    if (weekCursor == 0) {
      userEpoch = findTimestampUserEpoch(_ve, _tokenId, _startTime, maxUserEpoch);
    } else {
      userEpoch = userEpochOf[_tokenId];
    }

    if (userEpoch == 0) userEpoch = 1;

    IVe.Point memory userPoint = _ve.userPointHistory(_tokenId, userEpoch);
    if (weekCursor == 0) {
      weekCursor = (userPoint.ts + WEEK - 1) / WEEK * WEEK;
    }
    if (weekCursor >= lastTokenTime) {
      return ClaimCalculationResult(0, 0, 0, 0, false);
    }
    if (weekCursor < _startTime) {
      weekCursor = _startTime;
    }

    return calculateToDistribute(
      _tokenId,
      weekCursor,
      _lastTokenTime,
      userPoint,
      userEpoch,
      maxUserEpoch,
      _ve
    );
  }

  function calculateToDistribute(
    uint _tokenId,
    uint weekCursor,
    uint _lastTokenTime,
    IVe.Point memory userPoint,
    uint userEpoch,
    uint maxUserEpoch,
    IVe _ve
  ) public view returns (ClaimCalculationResult memory) {
    IVe.Point memory oldUserPoint;
    uint toDistribute;
    for (uint i = 0; i < 50; i++) {
      if (weekCursor >= _lastTokenTime) {
        break;
      }
      if (weekCursor >= userPoint.ts && userEpoch <= maxUserEpoch) {
        userEpoch += 1;
        oldUserPoint = userPoint;
        if (userEpoch > maxUserEpoch) {
          userPoint = IVe.Point(0, 0, 0, 0);
        } else {
          userPoint = _ve.userPointHistory(_tokenId, userEpoch);
        }
      } else {
        int128 dt = int128(int256(weekCursor - oldUserPoint.ts));
        uint balanceOf = uint(int256(_positiveInt128(oldUserPoint.bias - dt * oldUserPoint.slope)));
        if (balanceOf == 0 && userEpoch > maxUserEpoch) {
          break;
        }
        toDistribute += balanceOf * tokensPerWeek[weekCursor] / veSupply[weekCursor];
        weekCursor += WEEK;
      }
    }
    return ClaimCalculationResult(
      toDistribute,
      Math.min(maxUserEpoch, userEpoch - 1),
      weekCursor,
      maxUserEpoch,
      true
    );
  }

  function _positiveInt128(int128 value) internal pure returns (int128) {
    return value < 0 ? int128(0) : value;
  }

  /// @dev Block timestamp rounded to weeks
  function timestamp() external view returns (uint) {
    return block.timestamp / WEEK * WEEK;
  }

  /// @dev See {IERC165-supportsInterface}.
  function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
    return interfaceId == InterfaceIds.I_VE_DISTRIBUTOR || super.supportsInterface(interfaceId);
  }

}
